DROP DATABASE Mammals;
CREATE DATABASE Mammals;
USE Mammals;

CREATE TABLE Scientist(
scientist_id INTEGER NOT NULL AUTO_INCREMENT,
first_name VARCHAR(30) NOT NULL,
middle_name VARCHAR(30),
last_name VARCHAR(30) NOT NULL,
nationality CHAR(3),
birthday DATE, 
PRIMARY KEY (scientist_id)
);

CREATE TABLE Superorder(
superorder_id INTEGER NOT NULL AUTO_INCREMENT,
name VARCHAR(30) NOT NULL,
PRIMARY KEY (superorder_id)
);

CREATE TABLE Orderr(
order_id INTEGER NOT NULL AUTO_INCREMENT,
name VARCHAR(30) NOT NULL,
year INTEGER,
named_scientist INTEGER,
superorder INTEGER,
PRIMARY KEY (order_id),
FOREIGN KEY (named_scientist) REFERENCES Scientist(scientist_id)
	ON UPDATE CASCADE
    ON DELETE SET NULL,
FOREIGN KEY (superorder) REFERENCES Superorder(superorder_id)
	ON UPDATE CASCADE
    ON DELETE SET NULL
);

CREATE TABLE Family(
family_id INTEGER NOT NULL AUTO_INCREMENT,
name VARCHAR(30) NOT NULL,
year INTEGER,
orderr INTEGER NOT NULL,
named_scientist INTEGER,
PRIMARY KEY (family_id),
FOREIGN KEY (orderr) REFERENCES Orderr(order_id)
	ON UPDATE CASCADE
    ON DELETE CASCADE,
FOREIGN KEY (named_scientist) REFERENCES Scientist(scientist_id)
	ON UPDATE CASCADE
    ON DELETE SET NULL
);

CREATE TABLE Region(
region_id INTEGER NOT NULL AUTO_INCREMENT,
name VARCHAR(50) NOT NULL,
PRIMARY KEY (region_id)
);

CREATE TABLE Genus(
genus_id INTEGER NOT NULL AUTO_INCREMENT,
name VARCHAR(30) NOT NULL,
year INTEGER,
is_extinct TINYINT(1) NOT NULL,
family INTEGER NOT NULL,
named_scientist INTEGER,
PRIMARY KEY (genus_id),
FOREIGN KEY (family) REFERENCES Family(family_id)
	ON UPDATE CASCADE
    ON DELETE CASCADE,
FOREIGN KEY (named_scientist) REFERENCES Scientist(scientist_id)
	ON UPDATE CASCADE
    ON DELETE SET NULL
);

CREATE TABLE Conservation_Status(
status_id INTEGER NOT NULL AUTO_INCREMENT,
name VARCHAR(30) NOT NULL,
PRIMARY KEY (status_id)
);

CREATE TABLE Physical_Characteristics(
char_id INTEGER NOT NULL AUTO_INCREMENT,
name VARCHAR(30) NOT NULL,
PRIMARY KEY (char_id)
);

CREATE TABLE Species(
species_id INTEGER NOT NULL AUTO_INCREMENT,
name VARCHAR(50) NOT NULL,
nonscientific_name VARCHAR(100),
image TEXT,
year INTEGER,
avg_weight DOUBLE,
avg_height DOUBLE,
avg_lifespan DOUBLE,
avg_offspring DOUBLE,
avg_gestation DOUBLE,
behavior TEXT,
genus INTEGER NOT NULL,
status_id INTEGER,
named_scientist INTEGER,
PRIMARY KEY (species_id),
FOREIGN KEY (genus) REFERENCES Genus(genus_id)
	ON UPDATE CASCADE
    ON DELETE CASCADE,
FOREIGN KEY (status_id) REFERENCES Conservation_Status(status_id)
	ON UPDATE CASCADE
    ON DELETE SET NULL,
FOREIGN KEY (named_scientist) REFERENCES Scientist(scientist_id)
	ON UPDATE CASCADE
    ON DELETE SET NULL
);

CREATE TABLE Found_in(
region_id INTEGER NOT NULL,
species_id INTEGER NOT NULL,
PRIMARY KEY (region_id, species_id),
FOREIGN KEY (region_id) REFERENCES Region(region_id)
	ON UPDATE CASCADE
    ON DELETE CASCADE,
FOREIGN KEY (species_id) REFERENCES Species(species_id)
	ON UPDATE CASCADE
    ON DELETE CASCADE
);

CREATE Table Color(
color_id INTEGER NOT NULL AUTO_INCREMENT,
name VARCHAR(30) NOT NULL,
PRIMARY KEY (color_id)
);

Create Table Can_have(
species_id INTEGER NOT NULL,
color_id INTEGER NOT NULL,
char_id INTEGER NOT NULL,
PRIMARY KEY (species_id, color_id, char_id),
FOREIGN KEY (species_id) REFERENCES Species(species_id)
	ON UPDATE CASCADE
    ON DELETE CASCADE,
FOREIGN KEY (color_id) REFERENCES Color(color_id)
	ON UPDATE CASCADE
    ON DELETE CASCADE,
FOREIGN KEY (char_id) REFERENCES Physical_Characteristics(char_id)
	ON UPDATE CASCADE
    ON DELETE CASCADE
);

